mapper.sql:
FROM 
	infrNationality a
WHERE 1=1
	AND ifnaUseNy = #{shUseNy}
	AND ifnaDelNy = #{shDelNy}

		AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


		AND ifnaName LIKE CONCAT('%',#{shValue},'%')
		AND ifnaNameEng LIKE CONCAT('%',#{shValue},'%')
		AND ifnaCode2Char LIKE CONCAT('%',#{shValue},'%')
		AND ifnaCode3Char LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrNationality (
	ifnaName
	,ifnaNameKor
	,ifnaNameEng
	,ifnaCode2Char
	,ifnaCode3Char
	,ifnaUseNy
	,ifnaOrder
	,ifnaDesc
	,ifnaDelNy

)
VALUES (
	#{ifnaName}
	,#{ifnaNameKor}
	,#{ifnaNameEng}
	,#{ifnaCode2Char}
	,#{ifnaCode3Char}
	,#{ifnaUseNy}
	,#{ifnaOrder}
	,#{ifnaDesc}
	,0

)

	SELECT last_insert_id()

mapper.update:
UPDATE infrNationality
SET
	ifnaName = #{ifnaName}
	,ifnaNameKor = #{ifnaNameKor}
	,ifnaNameEng = #{ifnaNameEng}
	,ifnaCode2Char = #{ifnaCode2Char}
	,ifnaCode3Char = #{ifnaCode3Char}
	,ifnaUseNy = #{ifnaUseNy}
	,ifnaOrder = #{ifnaOrder}
	,ifnaDesc = #{ifnaDesc}

WHERE 1=1
	AND ifnaSeq = #{ifnaSeq}

mapper.delete:
DELETE FROM infrNationality
WHERE 1=1
	AND ifnaSeq = #{ifnaSeq}
