mapper.sql:
FROM 
	locnLocation a
WHERE 1=1
	 AND a.ltltLat BETWEEN #{xltltLatStart} AND #{xltltLatEnd}
	 AND a.ltltLng BETWEEN #{xltltLngStart} AND #{xltltLngEnd}
	AND a.ltltDelNy = #{shDelNy}


		AND a.ltltName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO locnLocation (
	ltltDefaultNy
	,ltltName
	,ltltEstDate
	,ltltStartDate
	,ltltCeo
	,ltltEmail
	,ltltPhone1
	,ltltPhone2
	,ltltTelecomCd
	,ltltMobile
	,ltltFax
	,ltltZipcode
	,ltltAddress1
	,ltltAddress2
	,ltltAddress3
	,ltltLat
	,ltltLng
	,ltltUseNy
	,ltltDesc
	,ltltDelNy

)
VALUES (
	#{ltltDefaultNy}
	,#{ltltName}

		,NULL
		,#{ltltEstDate}


		,NULL
		,#{ltltStartDate}

	,#{ltltCeo}
	,#{ltltEmail}
	,#{ltltPhone1}
	,#{ltltPhone2}
	,#{ltltTelecomCd}
	,#{ltltMobile}
	,#{ltltFax}
	,#{ltltZipcode}
	,#{ltltAddress1}
	,#{ltltAddress2}
	,#{ltltAddress3}
	,#{ltltLat}
	,#{ltltLng}
	,#{ltltUseNy}
	,#{ltltDesc}
	,0

)

	SELECT last_insert_id()

mapper.update:
UPDATE locnLocation
SET
	ltltDefaultNy = #{ltltDefaultNy}
	,ltltName = #{ltltName}
	,ltltEstDate = 

			NULL
			#{ltltEstDate}

	,ltltStartDate = 

			NULL
			#{ltltStartDate}

	,ltltCeo = #{ltltCeo}
	,ltltEmail = #{ltltEmail}
	,ltltPhone1 = #{ltltPhone1}
	,ltltPhone2 = #{ltltPhone2}
	,ltltTelecomCd = #{ltltTelecomCd}
	,ltltMobile = #{ltltMobile}
	,ltltFax = #{ltltFax}
	,ltltZipcode = #{ltltZipcode}
	,ltltAddress1 = #{ltltAddress1}
	,ltltAddress2 = #{ltltAddress2}
	,ltltAddress3 = #{ltltAddress3}
	,ltltLat = #{ltltLat}
	,ltltLng = #{ltltLng}
	,ltltUseNy = #{ltltUseNy}
	,ltltDesc = #{ltltDesc}

WHERE 1=1
	AND ltltSeq = #{ltltSeq}

mapper.delete:
DELETE FROM locnLocation 
WHERE 1=1
	AND ltltSeq = #{ltltSeq}
