mapper.sql:
FROM 
	mbccMajorComment a
WHERE 1=1
	AND a.mbmtSeq = #{mbmtSeq}
	AND mbctDelNy = #{shDelNy}

		AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO mbccMajorComment (
	mbctSeq
	,mbctComment
	,mbctScore
	,mbctDelNy

	,mbmtSeq
	,ifmmSeq			
)
VALUES (
	#{mbctComment}
	,#{mbctScore}
	,0

	,#{mbmtSeq}
	,#{ifmmSeq}			
)

	SELECT last_insert_id()

mapper.update:
UPDATE mbccMajorComment
SET
	mbctComment = #{mbctComment}
	,mbctScore = #{mbctScore}

WHERE 1=1
	AND mbctSeq = #{mbctSeq}

mapper.delete:
DELETE FROM mbccMajorComment
WHERE 1=1
	AND mbctSeq = #{mbctSeq}
